import { Button, Card, Container, makeStyles, TextField, Typography } from '@material-ui/core'
import Alert from '@material-ui/lab/Alert';
import React, { useState } from 'react';
import {useHistory} from 'react-router-dom'
import ContentDashboardLayout from '../../components/ContentSidebar/ContentDashboardLayout';
import  {db} from '../../firebase'
const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: '#141256',
  },
  form: {
    alignItems: "center",
    justifyContent: "center",
    width: '90%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
      background: "#ff7a00",
      color: "white",
      borderRadius: '20px',
      margin: theme.spacing(3, 0, 2),
  },
    wrapper: {
  display: 'flex',
  flex: '1 1 auto',
  overflow: 'hidden',
  paddingTop: 64,
  [theme.breakpoints.up('lg')]: {
    paddingLeft: 256
  },
  
  },
  container: {
      display: 'flex',
  flex: '1 1 auto',
  overflow: 'hidden'
  },
  content: {
  
      flex: '1 1 auto',
  height: '100%',
  overflow: 'auto'
    },
}));


const AddContent = ({match}) => {
  const classes= useStyles();
  const [title, setContentName] = useState('')
  const [desc, setDesc] = useState('')
  const [loading, setLoading] = useState(false);
  const [mid, setMid] = useState(match.params.id)
  const history = useHistory();

    
    const handleSubmit = (e) => {
    e.preventDefault();
    const content = {title, mid, desc};
    setLoading(true);
      db.collection('moduleData').add(content).then((data) =>{
        console.log(data)
        history.push(`/machine-data/${match.params.id}/Module`)
      })

  }
 
    return (
      <>
      <ContentDashboardLayout match={match}/>
        <div>   
          <div className={classes.wrapper}>
        <div className={classes.container}>
          <Card className={classes.content}>
           <div className={classes.paper}>
            <Alert severity="info">You are currently adding a new Module</Alert>
            <br/>
            <Typography component="h1" variant="h4">
          Add Module
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <TextField value={mid}
           fullWidth
           variant="outlined"
           margin="normal"
           label="Machine id"
           disabled
           onChange ={(e) => setMid(e.target.value)}
           />
          <TextField
          value={title}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="content_name"
            label="Module Title"
            name="content_name"
            autoFocus
            onChange={(e) => setContentName(e.target.value)}
            
          />
               <TextField
          value={desc}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            multiline
            rows={7}
            id="desc"
            label="Description"
            name="desc"
            autoFocus
            onChange={(e) => setDesc(e.target.value)}
            
          />
          
         {!loading && <Button
            type="submit"
            fullWidth
            variant="contained"
            
            className={classes.submit}
          >
            Add Module
            </Button>}
         {
           loading && <Button
            type="submit"
            fullWidth
            variant="contained"
            
            disabled
            className={classes.submit}
          >Adding Module...</Button>
         }   
          </form>
          </div>
          </Card>
        </div>
      </div>
      
           
         
          
        </div>
        </>
    )
}

export default AddContent
